<?php	header("Content-Type: application/json");	include ("../conexi.php");	$link = Conectarse();	$respuesta = array();	$query ="SELECT * FROM productos ORDER by descripcion";		$result=mysqli_query($link, $query) or die("Error en: $query  ".mysqli_error($link));		while($row = mysqli_fetch_assoc($result)){				$respuesta[]= $row["descripcion"];			}		echo json_encode($respuesta);?>