clude("scripts.php");?>		<script type="text/javascript" src="js/buscar_tarjeta.js" > </script>		<script>		$(document).ready(onLoad);				function onLoad(){						$("#enganche").keyup(function(e){				var enganche = Number($(this).val());				var importe = Number($("#importe").val());				var saldo_actual = importe -enganche;				$("#saldo_actual").val(saldo_actual);							});						$(".editar_venta").click( function cargarRegistro(e){				var id_registro = $(this).data("id_registro");								$.ajax({					"url": "funciones/fila_select.php",										"dataType": "JSON",					"data": {						"tabla": "ventas",						"id_campo": "id_ventas",						"id_valor": id_registro											}															}).done(function(respuesta){					console.log(respuesta.data)					$.each(respuesta.data, function(name, value){						if(name != "No."){							console.log("name", name);							console.log("value", value);							$("#"+ name).val(value);						}					});									});								$("#modal_venta").modal("show");							});									$("#form_venta").submit( function guardarRegistro(event){				event.preventDefault();								var boton = $(this).find(":submit");				var icono = boton.find(".fa");								boton.prop("disabled", true);				icono.toggleClass("fa-save fa-spinner fa-spin");								$.ajax({					url: "funciones/fila_update.php",					"method": "POST",					"data":					{						tabla: "ventas",						id_campo: "id_ventas",						id_valor: $("#id_ventas").val(),						valores: $("#form_venta").serializeArray()										}					}).done(function(respuesta){															 window.location.reload();										}).always(function (respuesta){										boton.prop("disabled", false);					icono.toggleClass("fa-save fa-spinner fa-spin");					alertify.success(respuesta.mensaje);				});												$("#modal_venta").modal("hide");							});			$("#btn_cargo").click(function(){				// $("#btn_cargo").html("");				$("#modal_cargo").modal("show");							});			$("#form_cargos").submit(function(event){				// $("#btn_cargo").html("");				event.preventDefault();				btn = $(this).find(":submit");								btn.prop("disabled", true);								$.ajax({					url: "control/guardar_cargos.php",					"method": "POST",					"data":					$("#form_cargos").serialize()										}).done(function(respuesta){										btn.prop("disabled", false);					alertify.success(respuesta.mensaje);					$("#modal_cargo").modal("hide");					window.location.reload();									});							});						$( "#producto_cargos" ).autocomplete({				source: "search_json.php?tabla=productos&campo=descripcion&valor=descripcion&etiqueta=descripcion",				minLength : 2,				select: function( event, ui ) {					console.log("item",ui.item );										$( "#importe_cargos" ).val(ui.item.extras.direccion);				}			});						$("#btn_ticket").click(function(){				$("#estado_cuenta").html("");				$.ajax({ 					url: "imprimir_contado.php",					data:{						tarjeta : $("#tarjeta").val()					}					}).done(function(respuesta){					$("#ticket").html(respuesta);					window.print();									})											});						$("#btn_venta").click(function(){				console.log("nueva_venta");				window.location.href="nueva_venta.php?tarjeta="+ $("#tarjeta").val();							});						$("#btn_estado_de_cuenta").click(function(){				$("#ticket").html("");				$.ajax({					url: "imprimir_estado_cuenta.php",					data:{						tarjeta : $("#tarjeta").val()					}					}).done(function(respuesta){					$("#estado_cuenta").html(respuesta);					window.print();									})							});					}			</script></body></html>																																					