<?php	header('Content-Type: application/json');	include("conexi.php");	$link=Conectarse();		$response = array();		$table = $_GET["table"];	$turno = $_GET["turno"];		$query_complete = "SELECT			`AUTO_INCREMENT` as nuevo_folio		FROM			information_schema.`TABLES`		WHERE			TABLE_SCHEMA = 'syncsis_muebleria'		AND TABLE_NAME = '$table'";		$result_complete = mysqli_query( $link, $query_complete  )	or die("Error al ejecutar consulta: $query_complete".mysqli_error($link));		$count_rows = mysqli_num_rows($result_complete);	$count = 0;		$response["filas"] = $count_rows ;	while($row = mysqli_fetch_assoc($result_complete)) {		$count++;				if($table == "Banco"){			$prefijo = "BANCO";			$response["data"] = $prefijo."-$turno-".$row["nuevo_folio"];		}		elseif($table == "Egresos"){			$prefijo = "EGR";			$response["data"] = $prefijo."-$turno-".$row["nuevo_folio"];		}		elseif($table == "Ventas"){			$response["data"] = $row["nuevo_folio"] ;		}		elseif($table == "compras"){			$response["data"] = $row["nuevo_folio"] ;		}	}			print(json_encode($response));	?>