<?php	include("conexi.php");	$link=Conectarse();		$query = "SELECT *  FROM mensajes LEFT JOIN usuarios USING(id_usuario) WHERE leido <> 1 LIMIT 5";		$result = mysqli_query($link, $query )	or die("Error al ejecutar consulta: $query".mysqli_error($link));			if(mysqli_num_rows($result) < 1){		?>		<li class="alert alert-warning li_mensaje">			<h4>				No hay Mensajes Nuevos. 			</h4>		</li>	<?php		}else{		while($row = mysqli_fetch_assoc($result)){ ?>			<li class="li_mensaje">				<a href="#" class="mensaje bg-info" data-id_mensaje="<?php echo $row["id_mensaje"];?>">					<strong><?php echo $row["nombre_usuario"];?></strong>					<span class="pull-right  ">						<?php						if($row["leido"] == 0){	?>														<i class="fa fa-envelope text-primary leido"></i>						<?php						}else{						?>							<i class="fa fa-check leido"></i>						<?php						}						?>																	</span>					<p class="small text-muted"><i class="fa fa-clock-o"></i> <?php echo date("d/m/Y", strtotime($row["fecha_mensaje"])).", ".  $row["hora_mensaje"];?></p>					<p><?php echo $row["mensaje"];?></p>					<span class="small text-muted hora_leido hide"><i class="fa fa-check fa-check"></i> Leido </span>									</a>  			</li>		 		<?php					}		?>		<?php}?>