<?php	include ("conex.php");	$link = Conectarse();	$response = array();	$articulos = array();	$cantidad_abono = array();			$query ="SELECT tarjeta , GROUP_CONCAT(articulo) articulo, SUM(cantidad_abono) AS cantidad_abono 		FROM ventas		WHERE ventas.estatus_pago = 'PENDIENTE'		GROUP BY tarjeta 		ORDER by tarjeta ";		$result=mysql_query($query,$link) or die("Error en: $query  ".mysql_error());		$tabla = array();			while($row = mysql_fetch_assoc($result)){				$articulos[]= $row["articulo"];		$cantidad_abono[]= $row["cantidad_abono"];	}		$response[]= $articulos;	$response[]= $cantidad_abono;	echo json_encode($response);?>