<?php	header("Content-Type: application/json");	include ("conex.php");	$link = Conectarse();	$ruta = $_POST["ruta"];	$respuesta = array();		$query ="SELECT * FROM ventas 	WHERE ventas.estatus_pago = 'PENDIENTE'	GROUP BY tarjeta 	ORDER by tarjeta ";		$result=mysql_query($query,$link) or die("Error en: $query  ".mysql_error());		while($row = mysql_fetch_assoc($result)){				$respuesta[] = $row["nombre_cliente"];	}		echo json_encode($respuesta);?>