<?php	header("Content-Type: application/json");	include ("conex.php");	$link = Conectarse();	$ruta = $_POST["ruta"];	$respuesta = array();	$query ="SELECT		suma_importe - suma_enganche - IF(ISNULL(total_abonado), 0, total_abonado) AS saldo_actual	FROM ventas 		LEFT JOIN 	(	SELECT	tarjeta,	IF(ISNULL(SUM(abono)), 0 ,SUM(abono))   AS total_abonado	FROM	abonos	GROUP BY tarjeta	) as t_abonado	USING(tarjeta)		LEFT JOIN 	(	SELECT	tarjeta,	SUM(cantidad_abono) AS suma_cantidad_abono,	SUM(importe) AS suma_importe,	SUM(enganche) AS suma_enganche	FROM	ventas	GROUP BY tarjeta	) as t_importe	USING(tarjeta)	WHERE ventas.estatus_pago = 'PENDIENTE'	GROUP BY tarjeta 	ORDER BY tarjeta ";		$result=mysql_query($query,$link) or die("Error en: $query  ".mysql_error());		while($row = mysql_fetch_assoc($result)){				$respuesta[] = $row["saldo_actual"];	}		echo json_encode($respuesta);?>