<?php	header("Content-Type: application/json");	include ("conex.php");	$link = Conectarse();	$ruta = $_POST["ruta"];	$respuesta = array();		$query ="SELECT	ventas.fecha_venta,	ventas.tarjeta,	ventas.nv,	ventas.nombre_cliente,	ventas.celular,	ventas.id_ventas,	ventas.telefono,	ventas.entre_calles,	ventas.referencias,	ventas.direccion,	ventas.sector,	ventas.clave_producto,	ventas.articulo,	ventas.clave_vendedor,	ventas.clave_cobrador,	ventas.fecha_vencimiento,	ventas.mes,	ventas.importe,	ventas.enganche,	ventas.saldo_inicial,	ventas.cobrador,	ventas.dia_cobranza,	ventas.fecha_hora_mod,	ventas.usuario_mod,	ventas.observaciones,	ventas.cantidad_abono,	ventas.fecha_ultimo_abono,	ventas.coordenadas,	ventas.id_estatus,	ventas.tipo_articulo,	importe - enganche - IF(ISNULL(total_abonado), 0, total_abonado) AS saldo_actual,	ventas.tipo_venta	FROM ventas 		LEFT JOIN 	(	SELECT	tarjeta,	IF(ISNULL(SUM(abono)), 0 ,SUM(abono))   AS total_abonado	FROM	abonos	GROUP BY tarjeta	) as t_abonado	USING(tarjeta)	WHERE ventas.estatus_pago = 'PENDIENTE'	GROUP BY tarjeta 	ORDER BY tarjeta	";		$result=mysql_query($query,$link) or die("Error en: $query  ".mysql_error());		while($row = mysql_fetch_assoc($result)){				$respuesta[] = $row["tarjeta"];	}		echo json_encode($respuesta);?>