<?php	include ("conex.php");	$link = Conectarse();		$query = "SELECT fecha_ultimo_abono FROM ventas		WHERE ventas.estatus_pago = 'PENDIENTE'	GROUP BY tarjeta 	ORDER BY tarjeta ";		$result = mysql_query($query,$link) or die("Error en: $query  ".mysql_error());		$tabla = array();		while($row = mysql_fetch_assoc($result)){		if($row["fecha_ultimo_abono"] != null){						$fecha_ultimo_abono = date("d-m-Y", strtotime($row["fecha_ultimo_abono"])); 		}		else{						$fecha_ultimo_abono = '';		}		$tabla[] = $fecha_ultimo_abono;	}	echo json_encode($tabla);?>