<?php	include ("conex.php");	include ("is_selected.php");	$link = Conectarse();	if($_POST["update"]){		$nombre_cobrador = $_POST["nombre_cobrador"];		$password = $_POST["password"];		$mac_impresora = $_POST["mac_impresora"];					foreach($nombre_cobrador as $key => $value){			if($value){				$update_cobradores ="UPDATE cobradores 				SET nombre_cobrador = '$value',				password = '$password[$key]',				mac_impresora = '$mac_impresora[$key]'				WHERE id_cobrador = '$key'";								mysql_query($update_cobradores) 				or die("Error actualizando: $update_cobradores ".mysql_error());										}		}		echo "<div class='mensaje'>Guardado Correctamente</div>";	}			if($_POST["action"] == "insert"){		$estatus = $_POST["estatus"];		$insert_estatus ="INSERT INTO estatus 		SET estatus = '$estatus'";				mysql_query($insert_estatus) 		or die("Error actualizando: $insert_estatus ".mysql_error());				echo "<div class='mensaje'>Guardado Correctamente</div>";	}?>