<?phpheader("Content-Type: application/json");include ("conexi.php");$link = Conectarse();$respuesta = array();$cobrador = $_POST["cobrador"];$comentario = $_POST["comentario"];$tarjeta = $_POST["tarjeta"]; $insert ="INSERT INTO incidencias		SET 		fecha_hora_inc = NOW(),  		tarjeta = '$tarjeta', 		tipo_inc = 'Comentario', 		comentario = '$comentario',				cobrador = '$cobrador'	";		$result = mysqli_query($link, $insert);if($result){		$respuesta["estatus"] ="success";	$respuesta["mensaje"] = "Incidencia Guardada";}else{		$respuesta["estatus"] ="error";	$respuesta["mensaje"] = "Error insertando detalle $insert: ".mysqli_error($link);	}echo json_encode($respuesta);?>