<?php	if($_POST["guardar"]){					$nombre_vendedor = $_POST["nombre_vendedor"];						foreach($nombre_vendedor as $key => $value){				if($value){					$update_vendedores ="UPDATE vendedores 					SET nombre_vendedor = '$value' WHERE id_vendedor = '$key'";										mysql_query($update_vendedores) 					or die("Error actualizando vendedores: $update_vendedores ".mysql_error());												}			}						echo "<div class='mensaje'>Guardado Correctamente</div>";			}?>