<?php	include ("conex.php");	include ("is_selected.php");	include ("classes/numero_a_letras.php");	$link = Conectarse();			if($_GET["tarjeta"]){		$tarjeta  = $_GET["tarjeta"];	}?><div class="borde"> 		<?php 		$q_tarjeta = "SELECT * , 		suma_importe - suma_enganche - IF(ISNULL(total_abonado), 0, total_abonado) AS saldo_calculado		FROM ventas 				LEFT JOIN 		(		SELECT		tarjeta,		IF(ISNULL(SUM(abono)), 0 ,SUM(abono))   AS total_abonado		FROM		abonos		WHERE		tarjeta = '$tarjeta'		) as t_abonado		USING(tarjeta)				LEFT JOIN 		estatus		USING(id_estatus)				LEFT JOIN 		(		SELECT		tarjeta,		SUM(importe) AS suma_importe,		SUM(enganche) AS suma_enganche		FROM		ventas		WHERE		tarjeta = '$tarjeta'		) as t_importe		USING(tarjeta)		WHERE tarjeta = '$tarjeta'";		$result_tarjeta=mysql_query($q_tarjeta,$link) or die("Error en: $q_tarjeta  ".mysql_error());		$rowcount=mysql_num_rows($result_tarjeta);				while($row = mysql_fetch_assoc($result_tarjeta)){			$nv = $row["nv"];			$tarjeta = $row["tarjeta"];			$fecha_venta = date("d/m/Y", strtotime($row["fecha_venta"]));			$fecha_larga =$row["fecha_venta"];			$fecha_venc_larga =$row["fecha_vencimiento"];			$fecha_vencimiento = date("d/m/Y", strtotime($row["fecha_vencimiento"]));			if($fecha_vencimiento == '31/12/1969'){				$fecha_vencimiento = '';			}			$nombre_cliente = $row["nombre_cliente"];			$direccion = $row["direccion"];			$referencias = $row["referencias"];			$telefono = $row["telefono"];			$entre_calles = $row["entre_calles"];			$dia_cobranza = $row["dia_cobranza"];			$clave_vendedor = $row["clave_vendedor"];			$saldo_actual = $row["saldo_calculado"];			$cobrador = $row["cobrador"];			$sector = $row["sector"];			$articulo = $row["articulo"];			$importe = $row["importe"];			$enganche = $row["enganche"];			$cantidad_abono  = $row["cantidad_abono"];			$estatus  = $row["estatus"];			$fecha_hora_mod  = $row["fecha_hora_mod"];			$usuario_mod  = $row["usuario_mod"];					}	?>		<div id="encabezado" class="row">				<div  class="col-xs-4 text-center">			<div id="logo">				<img src="img/logo.png" width="200" height="50"/>			</div>		</div>		<div id="rfc" CLASS="COL-XS-12">			<h6>				HILARIO ROBERTO LAGUNA LÓPEZ RFC LALH640228EK7 CURP LALH640228HMCGPL02 <br>				VENTA DE ARTICULOS PARA EL HOGAR  . TEL: (01591) 9185428<br>				AV 16 DE SEPTIEMBRE S/N SAN SEBASTIÁN, ZUMPANGO ESTADO DE MEXICO ,			</h6>		</div>	</div>		<div class="row">		<div  class="col-xs-3">			<h4 id="tipo_copia">				</h4>			</div>				<div  class="col-xs-3 text-right">		<label > NV:</label><?php echo $nv;?></span> 			</div>	<div  class="col-xs-3 text-right">		<h3><b>N°: <?php echo $tarjeta;?> </b></h3> 		</div>	<div  class="col-xs-3 ">		<div id="barcode" ></div>		</div>	</div><input type="hidden" id="tarjeta" value="<?php echo $tarjeta;?> "><div id="div_tarjeta" class="row">	<div class="col-xs-12">		<table BORDER="2" class="table table-condensed">			<tr>				<td>					<label  >  Fecha de Venta: </label>				</td>				<td>										<?php echo $fecha_venta;?>					&nbsp;				</td>				<td>					<span >  Fecha de Vencimiento:</span>				</td>				<td>					<?php echo $fecha_vencimiento;?>					&nbsp;				</td>											</tr>			<tr>							</tr>			<tr>							</tr>			<tr>				<td>					<span > Nombre :</span>				</td>				<td colspan="3">					<?php echo $nombre_cliente;?>				</td>			</tr>						<tr>				<td >					<span > Artículo</span>				</td>				<td colspan="3">					<?php echo $articulo;?>				</td>			</tr>			<tr>				<td>					<span > Teléfono:</span>				</td>				<td >					<?php echo $telefono;?>				</td>			</tr>			<tr>				<td>					<span>Importe Total</span>				</td>				<td>					$ <?php echo number_format($importe);?>				</td>			</tr>			<tr>				<td>					<span>Enganche</span>				</td>				<td>					$ <?php echo number_format($enganche);?>				</td>			</tr>			<tr>				<td>					<span>Saldo Actual</span>				</td>				<td>					$ <?php echo number_format($saldo_actual);?>				</td>			</tr> 			<tr>				<td>					<span>Abono Semanal</span>				</td>				<td>					<input size="10" type="text" name="cantidad_abono"  value="<?php echo $cantidad_abono;?>" />				</td>			</tr>		</table>	</div></div><div class="row">	<div class="col-xs-12">		<?			$q_abonos ="SELECT 			fecha_hora_abono,			'' AS cargo,			abono,			cobrador,			tipo_abono AS concepto,			comentario_abono AS comentario						FROM abonos WHERE tarjeta = '$tarjeta' 						UNION						SELECT 			CONCAT(fecha_venta, ' 00:00:00') AS fecha_hora_abono,			importe AS cargo,			'' AS abono,			clave_vendedor AS cobrador,			CONCAT('VENTA #', id_ventas) AS concepto,			articulo AS comentario						FROM ventas			WHERE tarjeta = '$tarjeta'  						UNION						SELECT 			CONCAT(fecha_venta, ' 00:12:00') AS fecha_hora_abono,			'' AS cargo,			enganche AS abono,			clave_vendedor AS cobrador,			CONCAT('ENGANCHE #', id_ventas) AS concepto,			articulo AS comentario						FROM ventas			WHERE tarjeta = '$tarjeta'  						ORDER BY fecha_hora_abono   ";			$result_abonos = mysql_query($q_abonos,$link) or die("Error en: $q_abonos  ".mysql_error());						if(mysql_num_rows($result_abonos)< 1 ){								echo "<div class='titulo'> No hay abonos registrados</div>";				}else{			?>			<br>				<table class="table table-bordered table-condensed table-striped">				<thead>					<th>FECHA</th>					<th>SALDO ANTERIOR</th>					<th>CARGOS</th>					<th>ABONOS</th>					<th>SALDO RESTANTE</th>					<th>COBRADOR/VENDEDOR</th>					<th>CONCEPTO</th>					<th>Comentarios </th>					<th>-</th>				</thead>								<tbody>					<?php 												$saldo_anterior = 0;						$total_cargos = 0;						$total_abonos = 0;												while($row = mysql_fetch_assoc($result_abonos)){							$fecha_abono =  date("d/m/Y", strtotime($row["fecha_hora_abono"]));							$fecha_hora_abono = $row["fecha_hora_abono"];							setlocale(LC_TIME, "es_ES");							$dia_numerico = date("w", strtotime($row["fecha_hora_abono"])) ;							$dias = array("Domingo","Lunes","Martes","Miércoles","Jueves","Viernes","Sábado");														//$dia_cobrado = strftime ("%A", 2);														if($row["cargo"] > 0){																$saldo_restante = $saldo_anterior + $row["cargo"] ;								$total_cargos+= $row["cargo"];							}														else{																$saldo_restante = $saldo_anterior - $row["abono"];								$total_abonos += $row["abono"];							}														$cobrador = $row["cobrador"];														$comentario = $row["comentario"];						?>						<tr>							<td>								<?php echo $dias[$dia_numerico], " ", $fecha_abono ;?>							</td>							<td>								<?php echo "$ ". number_format($saldo_anterior,2);?>							</td>							<td><?php echo  number_format($row["cargo"]);?></td>							<td><?php echo  number_format($row["abono"]);?></td>							<td>								<?php echo "$ ". number_format($saldo_restante,2);?>							</td>							<td>								<?php echo $cobrador;?>							</td>							<td>								<?php echo $row["concepto"];?>							</td>							<td>								<?php echo $comentario;?>							</td>							<td class="no_imprimir">								<?php 																		if($_COOKIE["usuario"] != "Tienda"){?>																		<button data-id_abono="<?php echo $fecha_hora_abono;?>" data-tarjeta="<?php echo $tarjeta;?>" class="editar_abono btn btn-warning ">										<i class="fa fa-edit"></i>									</button>									<button id="<?php echo $fecha_hora_abono;?>"  data-tarjeta="<?php echo $tarjeta;?>" class="borrar btn btn-danger">										<i class="fa fa-times"></i>									</button>									<?																			}								?>							</td>						</tr> 						<?php														$saldo_anterior = $saldo_restante;						}					?>									</tbody>				<tfoot>					<tr>						<td></td>						<td></td>						<td>	<?php echo number_format($total_cargos)?></td>						<td><?php echo number_format($total_abonos)?></td>					</tr> 				</tfoot>			</table>			<?php			}			?>			</div></div></div>