<?php	header('Content-Type: application/json');	include("../../../conexi.php");	$link=Conectarse();		$respuesta = array();		$id_usuario = $_GET["id_usuario"];		$mensaje_error = "No encontrado";		$consulta_usuario = "SELECT *  FROM usuarios WHERE id_usuario = '$id_usuario' ";		$result_usuario = mysqli_query( $link, $consulta_usuario ) 	or die("Error al ejecutar consulta: $consulta_usario".mysqli_error($link));		while($fila_usuario = mysqli_fetch_assoc($result_usuario)) {		$respuesta["data"]["usuarios"] = $fila_usuario ;	}			$consulta_permisos = "SELECT *  FROM permisos WHERE id_usuarios = '$id_usuario' ";		$result_permisos = mysqli_query( $link, $consulta_permisos ) 	or die("Error al ejecutar consulta: $consulta_permisos".mysqli_error($link));		while($fila_permisos = mysqli_fetch_assoc($result_permisos)) {		$respuesta["data"]["permisos"][] = $fila_permisos ;	}				print(json_encode($respuesta));	?>