<?php	include ("conex.php");	$link = Conectarse();	$response = array();		//var_dump($_POST);	//$cols = array();	$cant_visitas = $_POST["count_rows"];	$tarjeta = $_POST["tarjeta"];	$cobrador = $_POST["cobrador"];	$fecha_visita = $_POST["fecha_visita"];	$hora_visita = $_POST["hora_visita"];	$location_visita = $_POST["location_visita"];	$count = 0;	foreach($tarjeta as $index => $value){				if($value){			$insert_visitas ="INSERT IGNORE INTO visitas					SET 					cobrador = '$cobrador[$index]', 					tarjeta = '$tarjeta[$index]', 					fecha_visita = '$fecha_visita[$index]', 					hora_visita = '$hora_visita[$index]',									location_visita = '$location_visita[$index]'										";			//echo $insert_venta ;					mysql_query($insert_visitas) or die("Error insertando detalle $insert_visitas: ".mysql_error());			$count++;					}	}		$response["rows_to_sync"] = $cant_visitas;	$response["rows_synced"] = $count;	if($response["rows_to_sync"] == $response["rows_synced"]){		$response["mensaje"] = "$count Visitas Cargadas Correctamente";		$response["status_sync"] = "success";	}else{				$response["mensaje"] = "Error, solo se syncronizaron $count de $cant_visitas visitas. ";		$response["status_sync"] = "error";	}	echo json_encode($response);	?>  