<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$response = array();	$debug = array();		$term = $_GET["term"];		$tabla = $_GET["tabla"];	$campo = $_GET["campo"];	$valor = $_GET["valor"];	$etiqueta = $_GET["etiqueta"];	$arr_pairs = $_GET["fields_value"];	$value = $_GET["value"];			//$query_complete = "SELECT $campo FROM $tabla  ";	//$query_complete = "SELECT $valor, $etiqueta,  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";	$query_complete = "SELECT *  FROM $tabla WHERE $campo LIKE '%" . $term . "%'";		if(isset( $_GET["order"])){		$order = $_GET["order"];		$query_complete.= "ORDER BY $order";			}		$result_complete = mysqli_query( $link, $query_complete )	or die("Error al ejecutar consulta: $query_complete".MYSQLi_ERROR($link));		$count = 0;	while($row = mysqli_fetch_assoc($result_complete)) {		$count++;		$precios = [];		$consulta_precios = "SELECT * FROM productos_precios LEFT JOIN tipo_precio USING(id_tipo_precio) WHERE id_articulo = '{$row["id_articulo"]}' AND activo=1";				$result_precios = mysqli_query( $link, $consulta_precios );				while($fila_precio = mysqli_fetch_assoc($result_precios)) {			$precios[] =$fila_precio;					}				array_push($response, 		array("value" => $row["$campo"], 		"label" => $row["$etiqueta"] ,		"extras" => $row ,		"precios" => $precios		));			}			print(json_encode($response));		?>