			if(isset($_GET["buscar"])){					$cobrador = $_GET["cobrador"];					$fecha_inicial = $_GET["fecha_inicial"];					$fecha_final = $_GET["fecha_final"];										$dia_fecha_inicial = date_create_from_format("d/m/Y", $fecha_inicial);					$dia_fecha_inicial = $dia_fecha_inicial->getTimestamp();					$dia_fecha_inicial = date("w", $dia_fecha_inicial);					$dia_fecha_inicial = $dias[$dia_fecha_inicial];										$dia_fecha_final = date_create_from_format("d/m/Y", $fecha_final);					$dia_fecha_final = $dia_fecha_final->getTimestamp();					$dia_fecha_final = date("w", $dia_fecha_final);					$dia_fecha_final = $dias[$dia_fecha_final];																			?>				<h4 class="visible-print">					<div class="row">						<div class="col-sm-6">							Reporte de Cobranza del : <?php echo $fecha_inicial." al ".$fecha_final;  ?>  						</div>						<div class="col-sm-6">							Cobrador :  <?php echo $cobrador;?>   						</div>					</div>					<div class="row">						<div class="col-sm-6">							Tarjetas Cobradas :  <?php echo $num_rows;?>						</div>						<div class="col-sm-6">													</div>					</div>				</h4>								<div class="btn_imprimir no_imprimir">					<button type="button" class="btn btn-primary" onclick="javascript:window.print();">						<i class="fa fa-print"> </i> Imprimir					</button>					<button type="button" class="btn btn-success btn_exportar" >						<i class="fa fa-arrow-right"> </i> Exportar					</button>				</div>								<table border="1" id="tabla_reporte" class="tabla_reporte">					<thead>						<th>COBRADOR</th>						<th>JUEVES</th>						<th>VIERNES</th>						<th>SABADO</th>						<th>DOMINGO</th>						<th>LUNES</th>						<th>MARTES</th>						<th>MIERCOLES</th>						<th>ACUMULADO</th>						<th>SUELDO</th>					</thead>						<tbody>													<?php							$total_lunes = array();							$total_martes = array();							$total_miercoles= array();							$total_jueves = array();							$total_viernes = array();							$total_sabado = array();							$total_domingo = array();							$total_sueldos = array();														$acumulado_semanal = array();														$q_cobradores = "SELECT * FROM cobradores";							$result_cobradores=mysql_query($q_cobradores,$link) or die("Error en: $q_ventas  ".mysql_error());																					while($row = mysql_fetch_assoc($result_cobradores)){								$cobrador = $row["nombre_cobrador"];																								$q_abonos ="								SELECT * FROM 								(SELECT  SUM(abono) as lunes FROM abonos 								WHERE DATE(fecha_hora_abono)								BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 0 AND tipo_abono <> 'Devolución') AS lunes,								(SELECT  SUM(abono) as martes FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 1 AND tipo_abono <> 'Devolución') AS martes,								(SELECT  SUM(abono) as miercoles FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 2 AND tipo_abono <> 'Devolución') AS miercoles,								(SELECT  SUM(abono) as jueves FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 3 AND tipo_abono <> 'Devolución') AS jueves,								(SELECT  SUM(abono) as viernes FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 4 AND tipo_abono <> 'Devolución') AS viernes,								(SELECT  SUM(abono) as sabado FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 5 AND tipo_abono <> 'Devolución') AS sabado,								(SELECT  SUM(abono) as domingo FROM abonos 								WHERE DATE(fecha_hora_abono) BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 								AND str_to_date('$fecha_final','%d/%m/%Y')								AND cobrador = '$cobrador' AND WEEKDAY(DATE(fecha_hora_abono)) = 6 AND tipo_abono <> 'Devolución') AS domingo";																$result_abonos=mysql_query($q_abonos,$link) or die("Error en: $q_abonos  ".mysql_error());																while($row = mysql_fetch_assoc($result_abonos)){									$lunes = $row["lunes"];									$martes = $row["martes"];									$miercoles = $row["miercoles"];									$jueves = $row["jueves"];									$viernes = $row["viernes"];									$sabado = $row["sabado"];									$domingo = $row["domingo"];																		if($lunes == ''){$lunes = 0;}									if($martes == ''){$martes = 0;}									if($miercoles == ''){$miercoles = 0;}									if($jueves == ''){$jueves = 0;}									if($viernes == ''){$viernes = 0;}									if($sabado == ''){$sabado = 0;}									if($domingo == ''){$domingo = 0;}																		$total_lunes[] = $lunes;									$total_martes[] = $martes;									$total_miercoles[] = $miercoles;									$total_jueves[] = $jueves;									$total_viernes[] = $viernes;									$total_sabado[] = $sabado;									$total_domingo[] = $domingo;																		$total = $lunes + $martes + $miercoles + $jueves + $viernes + $sabado + $domingo;									$sueldo = $total *.05;																		$acumulado_semanal[] = $total;									$total_sueldos[] = $sueldo;								?>									<tr class="number_format">									<td >										<?php echo "$cobrador";?>									</td>																		<td class="number_format">										<?php echo "$jueves";?>									</td>									<td class="number_format">										<?php echo "$viernes";?>									</td>									<td class="number_format">										<?php echo "$sabado";?>									</td>									<td class="number_format">										<?php echo "$domingo";?>									</td>									<td class="number_format"><?php echo "$lunes";?>									</td>									<td class="number_format">										<?php echo "$martes";?>									</td>									<td class="number_format">										<?php echo "$miercoles";?>									</td>									<td class="number_format">										$ <?php echo number_format($total, 2);?>									</td>									<td class="number_format">										$ <?php echo number_format($sueldo, 2);?>									</td>								</tr>								<?php								}							}						?>												<tr class="number_format">							<td ></td>							<td class="number_format"> <?php echo number_format(array_sum($total_jueves),2);?></td>										<td class="number_format"> <?php echo number_format( array_sum($total_viernes),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_sabado),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_domingo),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_lunes),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_martes),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_miercoles),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($acumulado_semanal),2);?></td>							<td class="number_format"> <?php echo number_format( array_sum($total_sueldos),2);?></td>						</tr>					</tbody>					</table>												<div>						<br>					<br>					Recibe ______________________				</div>				<?php				}			?>		</div>				<?php include("scripts.php");?>			</body></html