cion = "SELECT					estatus,					count(abonos.tarjeta) as num_tarjetas,					SUM(abono) as suma_abonos				FROM					abonos				INNER JOIN ventas ON ventas.tarjeta = abonos.tarjeta				WHERE					abonos.cobrador = '$cobrador'				AND 				DATE(fecha_hora_abono) 				BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') 					AND str_to_date('$fecha_final','%d/%m/%Y')				GROUP BY					estatus					";			$result_integracion = mysql_query($q_integracion,$link) or die("Error en: $q_cobradores  ".mysql_error());			while($row = mysql_fetch_assoc($result_integracion)){				$estatus = $row["estatus"];				$num_tarjetas = $row["num_tarjetas"];				$sum_abono_semanal = $row["suma_abonos"];								$total_tarjetas[] = $num_tarjetas;				$total_abonos[] = $sum_abono_semanal;						?>			<tr>				<td> <a href="reporte_cobranza.php?fecha_inicial=<?php echo $fecha_inicial;?>&fecha_final=<?php echo $fecha_final;?>&buscar=buscar&cobrador=<?php echo "$cobrador";?>&estatus=<?php echo "$estatus";?>" target="_blank"> <?php echo "$estatus";?> </a>		</td>				<td  class="number_format"> <?php echo "$num_tarjetas";?>		</td>				<td class="number_format"> 	<?php echo  number_format($sum_abono_semanal,2);?>	</td>							</tr>		<?php			}		?>		<tr>			<th>				TOTAL GENERAL			</th>			<th class="number_format"> 				<?php echo  array_sum($total_tarjetas);?>			</th>			<th class="number_format"> 				<?php echo  "$ ".number_format(array_sum($total_abonos),2);?>			</th>		</tr>		</table>		<div>			<br>		<br>		Recibe ______________________	</div>	<div class="debug etiqueta">			<?php		//echo $q_integracion;	?>	</div>	<?php	} //END if is set boton buscar	?>	</div></body></html>