ador" id="cobrador" class="form-control">									<?php 					$q_cobradores = "SELECT * FROM cobradores ORDER BY nombre_cobrador";					$result_cobradores=mysql_query($q_cobradores,$link) or die("Error en: $q_cobradores  ".mysql_error());										while($row = mysql_fetch_assoc($result_cobradores)){						$cobrador_db = $row["nombre_cobrador"];											?>					<option value="<?php echo $cobrador_db;?>" 						<?php echo is_selected($cobrador_db, $cobrador);?> > 						<?php echo $cobrador_db;?> 						</option>					<?php					}					?>				</select>			</div>							<button name="buscar" class="btn btn-success">				<i class="fa fa-search"></i> Buscar			</button>		</form>	</div>	<?php 	if(isset($_GET["buscar"])){						$q_vendedores = "SELECT clave_vendedor, SUM(abonos.abono) as acumulado, tipo_abono		FROM abonos 		LEFT JOIN ventas		USING(tarjeta)		WHERE DATE(fecha_hora_abono) BETWEEN STR_TO_DATE('$fecha_inicial', '%d/%m/%Y')		AND STR_TO_DATE('$fecha_final', '%d/%m/%Y')				";						if($cobrador != "TODOS" ){			$q_vendedores.=  " AND abonos.cobrador= '$cobrador'";		}		if($estatus != "TODOS" ){			$q_vendedores.=  " AND id_estatus= '$id_estatus'";		}						$q_vendedores.= " AND tipo_abono <>'Devolución'			GROUP BY clave_vendedor			ORDER BY clave_vendedor";				$result_vendedores=mysql_query($q_vendedores,$link) or die("Error en: $q_vendedores  ".mysql_error());			$num_rows = mysql_num_rows($result_vendedores);		if($num_rows == 0){			echo "<div class='titulo'>No hay registros </div>"; 		}else{										?>		<h4 class="visible-print">			<div class="row">				<div class="col-sm-6">					Reporte de Cobranza del : <?php echo $fecha_inicial." al ".$fecha_final;  ?>  				</div>			</div>			<div class="row">				<div class="col-sm-6">					Tarjetas Cobradas :  <?php echo $num_rows;?>				</div>			</div>		</h4>				<div class="modal-footer hidden-print">			<button type="button" class="btn btn-primary" onclick="javascript:window.print();">				<i class="fa fa-print"> </i> Imprimir			</button>			<button type="button" class="btn btn-success btn_exportar" >				<i class="fa fa-arrow-right"> </i> Exportar			</button>		</div>				<table border="1" id="tabla_reporte" class="tabla3">			<thead>				<th>Vendedor</th>				<th>Acumulado</th>			</thead>			<tbody>						<?php				$sum_abonos = array();				while($row = mysql_fetch_assoc($result_vendedores)){			$clave_vendedor = $row["clave_vendedor"];			$acumulado = $row["acumulado"] ;			$sum_abonos[] = $acumulado;		?>			<tr>				<td>					<?php echo $clave_vendedor;?>				</td>				<td>					<?php echo number_format($acumulado, 2);?>				</td>			</tr>		<?php		}		?>		<tr>				<td></td>			<td>				<b><?php echo  "$ ".number_format(array_sum($sum_abonos),2);?></b>			</td>		</tr>		</tbody>			</table>				<?php		}	}	?>		</div><?php include("scripts.php");?>	<script>	$( document ).ready(function() {		$(".btn_exportar").click(function(){						$('#tabla_reporte').tableExport(			{				type:'excel',				tableName:'Registros',				escape:'false'			});		});	}); 	</script></body></html>