ores = "SELECT * FROM cobradores ORDER BY nombre_cobrador";								$result_cobradores=mysql_query($q_cobradores,$link) or die("Error en: $q_cobradores  ".mysql_error());																while($row = mysql_fetch_assoc($result_cobradores)){									$cobrador_db = $row["nombre_cobrador"];																	?>								<option value="<?php echo $cobrador_db;?>" <?php echo is_selected($cobrador_db, $cobrador);?> > 									<?php echo $cobrador_db;?> 									</option>								<?php								}							?>						</select>					</td>				</tr>				<tr>					<td>						<span>Clave Vendedor</span>					</td>					<td>						<input size="10" type="text" name="clave_vendedor"  value="<?php echo $clave_vendedor;?>" />					</td>				</tr>				<tr>					<td>						<span>Sector</span>					</td>					<td>						<select name="sector">							<option > Elige Sector</option>							<?php 																								$q_sectores = "SELECT * FROM sectores ";								$result_sectores=mysql_query($q_sectores,$link) or die("Error en: $q_sectores  ".mysql_error());																while($row = mysql_fetch_assoc($result_sectores)){									$sector_db = $row["sector"];																						?>								<option value="<?php echo $sector_db;?>" <?php echo is_selected($sector_db, $sector);?>  >									<?php echo $sector_db;?>									</option>																								<?php								}							?>						</select>					</td>				</tr>				<tr>					<td>						<span>Artículo</span>					</td>					<td>						<input size="80" type="text" name="articulo"  value="<?php echo ($articulo);?>" />					</td>				</tr>				<tr>					<td>						<label > Tipo de Artículo</label>					</td>					<td>						<select required name="tipo_articulo">							<option value="">Elige...</option>							<option value="Mueble" <?php echo is_selected($tipo_articulo, "Mueble");?> >Mueble</option>							<option value="Caja" <?php echo is_selected($tipo_articulo, "Caja");?>>Caja</option>						</select>					</td>				</t