<?phpinclude ("conex.php");$link = Conectarse();$response = array();$fecha_abono = $_POST["fecha_abono"];$cobrador = $_POST["cobrador"];$hora_abono = $_POST["hora_abono"];$fecha_hora_abono = $fecha_abono . " ". $hora_abono;$tarjeta = $_POST["tarjeta"];$saldo_anterior = $_POST["saldo_anterior"];$abono = $_POST["abono"];$saldo_restante = $_POST["saldo_restante"];$tipo_abono = $_POST["tipo_abono"];$id_abono = $_POST["id_abono"]; $update_abono ="UPDATE abonos		SET 		fecha_hora_abono = STR_TO_DATE('$fecha_hora_abono', ' %d/%m/%Y %H:%i:%S' ),  		tarjeta = '$tarjeta', 		saldo_anterior = '$saldo_anterior', 		abono = '$abono', 		saldo_restante = '$saldo_restante',						tipo_abono = '$tipo_abono',						cobrador = '$cobrador'	WHERE fecha_hora_abono = '$id_abono'";if(mysql_query($update_abono)){		$response["mensaje"] = "Actualizado Correctamente";	$response["estatus_update"] = "success";}else{		$response["mensaje"] =  "Error Actualizando abono $update_abono: ".mysql_error();	$response["estatus_update"] = "error";}$update_saldo ="UPDATE ventas		SET 		saldo_actual = '$saldo_restante' 		WHERE tarjeta = '$tarjeta'";if(mysql_query($update_saldo)){		$response["saldo_actual"] = "$saldo_restante";	$response["mensaje_saldo"] = "El saldo actual se actualizó a: $saldo_restante ";	$response["estatus_update_saldo"] = "success";}else{		$response["mensaje_saldo"] =  "Error Actualizando saldo $update_saldo: ".mysql_error();	$response["estatus_update_saldo"] = "error";}echo json_encode($response);?>