txt_f_i == ''){				alert("Ingresa una Fecha Inicial") ;				return false;			}			if (txt_f_f == ''){				alert("Ingresa una Fecha Final") ;				return false;			}						//return true	;					}	</script></head><body><?phpinclude("header.php");?><div class="contenido">		<div class='titulo'>Reporte Semanal de Ventas </div>	<div class="fechas">	<form name="form_rep_mes" method="get" enctype="multipart/form-data" action="" onsubmit="return validar(this.name);"	>				<span class="etiqueta">					Fecha Inicial: <input size="10" type="text" name="fecha_inicial" id="fecha_inicial" value="<?php echo $fecha_inicial;?>" />				</span>				<span class="etiqueta">					Fecha Final: <input size="10" type="text" name="fecha_final" id="fecha_final" value="<?php echo $fecha_final;?>" />				</span>								<input class="boton" type="submit" name="buscar_fecha" value="Buscar" />	</form>	</div>	<?php 	if(isset($_GET["buscar_fecha"])){		$fecha_inicial = $_GET["fecha_inicial"];		$fecha_final = $_GET["fecha_final"];		echo "<div class='titulo'>Ventas del :  $fecha_inicial al $fecha_final </div>"; ?>				<table border="1" id="tabla_reporte">						<th>RUTA</th>			<th>Vendedor</th>			<th>LUNES</th>			<th>MARTES</th>			<th>MIERCOLES</th>			<th>JUEVES</th>			<th>VIERNES</th>			<th>SABADO</th>			<th>DOMINGO</th>			<th>TOTAL VENTA</th>			<th>SUELDO</th>									<?php						$q_vendedores = "SELECT DISTINCT ventas_camionetas.ruta, nombre_vendedor, ventas_camionetas.ruta 				FROM ventas_camionetas INNER JOIN vendedores				ON ventas_camionetas.ruta = vendedores.ruta";		$result_vendedores=mysql_query($q_vendedores,$link) or die("Error en: $q_ventas  ".mysql_error());					while($row = mysql_fetch_assoc($result_vendedores)){					$nombre_vendedor = $row["nombre_vendedor"];					$ruta = $row["ruta"];					$ruta = $row["ruta"];														$q_ventas ="					SELECT * FROM 					(SELECT  SUM(importe_venta) as lunes FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 0) AS lunes,					(SELECT  SUM(importe_venta) as martes FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 1) AS martes,					(SELECT  SUM(importe_venta) as miercoles FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 2) AS miercoles,					(SELECT  SUM(importe_venta) as jueves FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 3) AS jueves,					(SELECT  SUM(importe_venta) as viernes FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 4) AS viernes,					(SELECT  SUM(importe_venta) as sabado FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 5) AS sabado,					(SELECT  SUM(importe_venta) as domingo FROM ventas_camionetas 					WHERE fecha_venta BETWEEN str_to_date('$fecha_inicial','%d/%m/%Y') AND str_to_date('$fecha_final','%d/%m/%Y')					AND ruta = '$ruta' AND WEEKDAY(fecha_venta) = 6) AS domingo					";				$result_ventas=mysql_query($q_ventas,$link) or die("Error en: $q_ventas  ".mysql_error());					while($row = mysql_fetch_assoc($result_ventas)){					$lunes = $row["lunes"];					$martes = $row["martes"];					$miercoles = $row["miercoles"];					$jueves = $row["jueves"];					$viernes = $row["viernes"];					$sabado = $row["sabado"];					$domingo = $row["domingo"];										if($lunes == ''){$lunes = 0;}					if($martes == ''){$martes = 0;}					if($miercoles == ''){$miercoles = 0;}					if($jueves == ''){$jueves = 0;}					if($viernes == ''){$viernes = 0;}					if($sabado == ''){$sabado = 0;}					if($domingo == ''){$domingo = 0;}										$total = $lunes + $martes + $miercoles + $jueves + $viernes + $sabado + $domingo;					$sueldo = $total *.10;				//echo 	$q_ventas. "---------------------------";					?>			<tr>								<td>					<?php echo "$ruta";?>				</td>				<td>					<?php echo "$nombre_vendedor";?>				</td>				<td>					<?php echo "$lunes";?>				</td>				<td>					<?php echo "$martes";?>				</td>				<td>					<?php echo "$miercoles";?>				</td>				<td>					<?php echo "$jueves";?>				</td>				<td>					<?php echo "$viernes";?>				</td>				<td>					<?php echo "$sabado";?>				</td>				<td>					<?php echo "$domingo";?>				</td>				<td>					$ <?php echo number_format($total, 2);?>				</td>				<td>					$ <?php echo number_format($sueldo, 2);?>				</td>			</tr>		<?php			}		}		?>	</table>	<?php	}	?></div></body></html>